import React,{useState, setText} from 'react'
import NavBar from '../components/NavBar'
import Header1 from '../components/Header1/Header1'
import SubHeader1 from '../components/Subheader1/Subheader1'
import './style.css'  

const Extras = () => {

  const [hide, setHide] = useState(true);
  const [text, setText] = useState('');
    const handleChange = event => {
        setText(event.target.value);
        const message = event.target.value;
        console.log(message);
        if(String(message)==="Leya"){
            setHide(false)
        }else{
            setHide(true)
        }
    };

  return (
    <>
    <NavBar />
    <div className='container-mid'>
    <Header1 text={"Extras"}/>
    <SubHeader1 text={"Editoras de um fornecedor"} />
    <div className='input-section move-up'>
      <input onChange={handleChange} className="text-input" type="text" placeholder="Fornecedor"></input>
    </div>
    {!hide ? <div className="ExtraContainer">
        <div className="excel-previeww">
        <div className="rectangle-6" />
        <div className="rectangle-8" />
        <div className="rectangle-7" />
        <div className="rectangle-9" />
        <div className="editoras flex-col">
            <p className="txt-483">Editora</p>
            <p className="txt-837">ASA</p>
            <p className="txt-957">D. QUIXOTE</p>
            <p className="txt-202">CAMINHO</p>
        </div>
        </div>
    </div> : null}  
    </div>
    <div class="vectors-wrapper">
      <div className={hide? "extras-vector1" : "extras-vector1-down"}></div>
      <div className={hide? "extras-vector2" : "extras-vector2-down"}></div>
    </div>

    </>
  )
}

export default Extras
