export default function ImportBtn() {
    const [state, setState] = useState(1);

    useEffect(() => {
        const timer = setTimeout(() => {
          console.log('This will run after n ms!')
        }, 1000);
        return () => clearTimeout(timer);
      }, []);

    const handleClick = event => {
        event.preventDefault();
        if (state === 1){
            setTimeout(() => {
                setState(2);
                console.log('This will run after 250 ms!')
                if (state === 2){
                    setTimeout(() => {
                        setState(3);
                        console.log('This will run after 2250 ms!')
                    }, 2250);
                }
            }, 250);

        }



    }



    return (


        <button className={state===1 ? "botao-importar" : state===2 ? "onclic" : "validate"} id="button" onClick={handleClick}></button>
    )
}
