import React from "react";
import Autocomplete from "react-autocomplete";
import "./style.css";


export default function TextInputM(props) {
    /*const autoComplete will autocomplete id=inputBox with 3 words: Leya, Manuel, Anotnio*/ 

    var value = 'asdasd';



    return(
        <div className="flex-col-center-vstart move-down" >
            <input className="text-input" id="inputBox" type="text" placeholder={props.text}></input>
            <AutoComplete
                getItemValue={(item) => item.name}
                items={members.filter((member) => member.name.includes(name))}
                renderItem={(item, isHighlighted) => (
                <div
                    style={{
                    verticalAlign: "middle",
                    background: isHighlighted ? "lightgray" : "white"
                    }}
                >
                    <a
                    target="_blank"
                    rel="noreferrer"
                    href={item.twitter}
                    style={{
                        border: "none",
                        backgroundColor: "none",
                        padding: 0,
                        dipslay: "inline-block"
                    }}
                    >
                    <img
                        alt="icon"
                        src={item.icon}
                        style={{ width: 30, height: 30 }}
                    />
                    </a>
                    <div style={{ display: "inline-block", minWidth: 200 }}>
                    {item.name}
                    </div>
                </div>
                )}
                wrapperStyle={{
                position: "relative",
                border: "solid 1px #800"
                }}
                menuStyle={{
                border: "solid 2px #080",
                backgroundColor: "#dfd",
                zIndex: 2,
                position: "absolute",
                top: 30,
                left: 0,
                overflow: "auto",
                maxHeight: 100
                }}
                value={name || ""}
                inputProps={{
                placeholder: "input name",
                style: { fontSize: 14, width: "100%", padding: 3 }
                }}
                onChange={(e) => setName(e.target.value)}
                onSelect={(name) => setName(name)}
            />
        </div>
    )
}
